			void setUniform(char * name, const float a) { __glewUniform1f(uniforms[name], a); } void setUniform(char * name, const float a, const float b) { __glewUniform2f(uniforms[name], a, b); } void setUniform(char * name, const float a, const float b, const float c) { __glewUniform3f(uniforms[name], a, b, c); } void setUniform(char * name, const float a, const float b, const float c, const float d) { __glewUniform4f(uniforms[name], a, b, c, d); }; void setUniform(char * name, const Size arraySize, const float* a) { __glewUniform1fv(uniforms[name], arraySize, a); } void setUniform(char * name, const Size componentSize, const Size arraySize, const float* a) { if( componentSize == 1 ) __glewUniform1fv(uniforms[name], arraySize, a); else if( componentSize == 2 ) __glewUniform2fv(uniforms[name], arraySize, a); else if( componentSize == 3 ) __glewUniform3fv(uniforms[name], arraySize, a); else if( componentSize == 4 ) __glewUniform4fv(uniforms[name], arraySize, a); }; void setUniform(char * name, const mc::Vector<float, 1> v) { __glewUniform1f(uniforms[name], v[0]); } void setUniform(char * name, const mc::Vector<float, 2> v) { __glewUniform2f(uniforms[name], v[0], v[1]); } void setUniform(char * name, const mc::Vector<float, 3> v) { __glewUniform3f(uniforms[name], v[0], v[1], v[2]); } void setUniform(char * name, const mc::Vector<float, 4> v) { __glewUniform4f(uniforms[name], v[0], v[1], v[2], v[3]); };;
