			//setUniform with unsigned int
			void setUniform(char * name, const unsigned int a) { __glewUniform1ui(uniforms[name], a); } void setUniform(char * name, const unsigned int a, const unsigned int b) { __glewUniform2ui(uniforms[name], a, b); } void setUniform(char * name, const unsigned int a, const unsigned int b, const unsigned int c) { __glewUniform3ui(uniforms[name], a, b, c); } void setUniform(char * name, const unsigned int a, const unsigned int b, const unsigned int c, const unsigned int d) { __glewUniform4ui(uniforms[name], a, b, c, d); }; void setUniform(char * name, const Size arraySize, const unsigned int* a) { __glewUniform1uiv(uniforms[name], arraySize, a); } void setUniform(char * name, const Size componentSize, const Size arraySize, const unsigned int* a) { if( componentSize == 1 ) __glewUniform1uiv(uniforms[name], arraySize, a); else if( componentSize == 2 ) __glewUniform2uiv(uniforms[name], arraySize, a); else if( componentSize == 3 ) __glewUniform3uiv(uniforms[name], arraySize, a); else if( componentSize == 4 ) __glewUniform4uiv(uniforms[name], arraySize, a); }; void setUniform(char * name, const mc::Vector<unsigned int, 1> v) { __glewUniform1ui(uniforms[name], v[0]); } void setUniform(char * name, const mc::Vector<unsigned int, 2> v) { __glewUniform2ui(uniforms[name], v[0], v[1]); } void setUniform(char * name, const mc::Vector<unsigned int, 3> v) { __glewUniform3ui(uniforms[name], v[0], v[1], v[2]); } void setUniform(char * name, const mc::Vector<unsigned int, 4> v) { __glewUniform4ui(uniforms[name], v[0], v[1], v[2], v[3]); };;

